INSERT INTO USER_APPEAL (user_appeal_id, user_id, tag, name, status, created_at, updated_at) VALUES
(1, 2, 'bug', 'Не воспроизводится видео на Samsung TV', 'open', '2024-01-15 19:30:00+03', '2024-01-15 19:30:00+03'),
(2, 3, 'feature', 'Добавить функцию скачивания серий для iOS', 'open', '2024-01-14 21:15:00+03', '2024-01-14 21:15:00+03'),
(3, 4, 'other', 'Проблема с оплатой подписки', 'open', '2024-01-13 20:45:00+03', '2024-01-13 20:45:00+03'),
INSERT INTO USER_APPEAL_MESSAGE (user_appeal_message_id, user_appeal_id, is_response, message_content, created_at, updated_at) VALUES
(1, 2, FALSE, 'При попытке воспроизвести любой контент на телевизоре Samsung UE65RU7470UXCE получаю черный экран. Звук есть, но видео не показывает. На других устройствах все работает нормально.', '2024-01-15 19:30:00+03', '2024-01-15 19:30:00+03'),
(2, 2, FALSE, 'Проблема началась после обновления прошивки телевизора до версии T-KTM2DEUC-3502. До этого все работало нормально.', '2024-01-15 19:35:00+03', '2024-01-15 19:35:00+03'),
(3, 3, FALSE, 'На устройствах Apple iPhone и iPad нет функции скачивания контента для офлайн-просмотра. На Android эта функция есть. Когда планируется добавить для iOS?', '2024-01-14 21:15:00+03', '2024-01-14 21:15:00+03'),
(4, 4, FALSE, 'При попытке оплатить подписку картой Visa получаю ошибку "Платеж не прошел". Карта активна, средства есть. Пробовал разные карты - результат тот же.', '2024-01-13 20:45:00+03', '2024-01-13 20:45:00+03'),
(5, 5, FALSE, 'При просмотре любого сериала субтитры работают только первые 10-15 минут, потом пропадают. Перезагрузка приложения временно помогает.', '2024-01-12 18:20:00+03', '2024-01-12 18:20:00+03'),
(6, 5, FALSE, 'Проблема наблюдается только в браузере Chrome на Windows. В других браузерах субтитры работают нормально.', '2024-01-13 09:15:00+03', '2024-01-13 09:15:00+03'),
(7, 6, FALSE, 'Хочу создать отдельный профиль для ребенка 6 лет, но нет возможности установить PIN-код для ограничения доступа к взрослому контенту.', '2024-01-11 22:30:00+03', '2024-01-11 22:30:00+03'),
(8, 7, FALSE, 'Даже при хорошей скорости интернета (100 Мбит/с) качество видео автоматически переключается на 480p. Принудительная установка качества не помогает.', '2024-01-10 17:40:00+03', '2024-01-10 17:40:00+03'),
(9, 8, FALSE, 'После просмотра сериала он остается в рекомендациях. Хотелось бы иметь возможность скрыть просмотренный контент из главной ленты.', '2024-01-09 19:15:00+03', '2024-01-09 19:15:00+03'),
(10, 9, FALSE, 'При попытке войти в аккаунт с нового устройства запрашивает код подтверждения, но SMS на телефон не приходит. Проверил спам - пусто.', '2024-01-08 16:50:00+03', '2024-01-08 16:50:00+03'),
(11, 10, FALSE, 'Приложение на Samsung Galaxy S21 вылетает когда пытаюсь перемотать серию более чем на 10 минут вперед. В логе ошибка: OutOfMemory Exception.', '2024-01-07 21:25:00+03', '2024-01-07 21:25:00+03'),
(12, 11, FALSE, 'Много турецких сериалов, но нет турецких субтитров. Для не слышащих пользователей это критично. Можно добавить поддержку?', '2024-01-06 20:10:00+03', '2024-01-06 20:10:00+03'),
(13, 12, FALSE, 'При воспроизведении фильмов с 5.1 звуком на домашнем кинотеатре Sony HT-ST5000 звук периодически пропадает на 2-3 секунды.', '2024-01-05 18:30:00+03', '2024-01-05 18:30:00+03'),
(14, 13, FALSE, 'Часто забываю на чем остановился в просмотре. Хотелось бы видеть большую кнопку "Продолжить просмотр" вверху главного экрана.', '2024-01-04 19:20:00+03', '2024-01-04 19:20:00+03'),
(15, 14, FALSE, 'Нажимаю "Забыли пароль?", ввожу email, но письмо для восстановления не приходит. Проверял папку спам - тоже нет.', '2024-01-03 17:15:00+03', '2024-01-03 17:15:00+03'),
